package lab.leetcode;

import java.util.ArrayList;
import java.util.List;

/**
 * @author jasonsong
 * 2018/12/1
 */


public class CheckInclusion {
    public static void main(String[] args) {
        String s1 = "";
        String s2 = "";
        CheckInclusion checkInclusion = new CheckInclusion();
        System.out.println(checkInclusion.checkInclusion("abc", "bbbca"));
        System.out.println(checkInclusion.checkInclusion(s1, s2));
    }

    public boolean checkInclusion(String s1, String s2) {
        char[] chars1 = s1.toCharArray();
        char[] chars2 = s2.toCharArray();
        List<String> list = new ArrayList<>();
        for (char c : chars1) {
            list.add("" + c);
        }
        List<String> tmpList;
        for (int i = 0; i < s2.length() - s1.length() + 1; i++) {
            tmpList = new ArrayList<>(list);
            for (int j = i; j < s1.length() + i; j++) {
                if (!tmpList.contains("" + chars2[j])) {
                    break;
                } else {
                    tmpList.remove("" + chars2[j]);
                }
            }
            if (tmpList.isEmpty()) {
                return true;
            }
        }
        return false;
    }


}
